void colores_logo(int x, int y)
{
	for (int f = 0; f < FIL; f++)
	{
		Console::SetCursorPosition(x, y + f);
		for (int c = 0; c < COL; c++)
		{
			switch (logo[f][c]) {
			case 0: Console::ForegroundColor = ConsoleColor::Cyan; break;
			case 1: Console::ForegroundColor = ConsoleColor::DarkCyan; break;
			case 2: Console::ForegroundColor = ConsoleColor::DarkYellow; break;
			case 3: Console::ForegroundColor = ConsoleColor::DarkRed; break;
			case 4: Console::ForegroundColor = ConsoleColor::Black; break;
			case 5: Console::ForegroundColor = ConsoleColor::Yellow; break;
			case 6: Console::ForegroundColor = ConsoleColor::DarkGray; break;
			case 7: Console::ForegroundColor = ConsoleColor::DarkBlue; break;
			case 8: Console::ForegroundColor = ConsoleColor::Gray; break;
			case 9: Console::ForegroundColor = ConsoleColor::Blue; break;
			}
			cout << (char)219;
		}
		cout << endl;
	}
}

